import axios from 'axios'

axios.defaults.baseURL= '/api'

axios.interceptors.request.use(request => {
  const token = window.localStorage.getItem('token');
  if (token) {
    request.headers['Authorization'] =`Bearer ${token}`;
  }
  return request;
});

export const login = form => {
  return axios.post('/user/login', form)
  .then( res => {
    return res.data
  })
  .catch(e => {
    throw e.response.data
  })
}

export const register = form => {
  return axios.post('/user/register', form)
  .then( res => {
    return res.data
  })
  .catch( e => {
    throw e.response.data
  })
}

export const user = () => {
  return axios.get('/user')
  .then ( res => {
    return res.data
  })
  .catch( e => {
    throw e.response.data
  })
}

export const getCash = () => {
  return axios.get('/stock/cash')
  .then( res => {
    return res.data
  })
  .catch( e => {
    throw e.response.data
  })
}

export const getPortfolio = () => {
  return axios.get('/stock/portfolio')
  .then( res => {
    return res.data
  })
  .catch( e => {
    throw e.response.data
  })
}

export const getTransactions = () => {
  return axios.get('/stock/transactions')
  .then( res => {
    return res.data
  })
  .catch( e => {
    throw e.response.data
  })
}

export const buyStock = form => {
  return axios.get('/stock/buy', form)
  .then( res => {
    return res.data
  })
  .catch( e => {
    throw e.response.data
  })
}


export const sellStock = form => {
  return axios.post('/stock/sell', form)
  .then( res => {
    return res.data
  })
  .catch( e => {
    throw e.response.data
  })
}
