import React, { Component } from 'react';
import { Row, Col, Icon, Form, Button, Input } from 'antd';
import { sellStock } from '../apis';

export default class Share extends Component {
  handleSubmit = (stockNumber) => {
    console.log(stockNumber);
    // const { validateQty, validateSymbol } = this.state
    // e.preventDefault()
    // this.props.form.validateFields( async(err, values) => {
    //   if (!err && validateQty === 'success' && validateSymbol === 'success') {
    //     let res = await sellStock(values)
    //     alert(res)
    //     window.location.reload()
    //   }
    // })
  };

  render() {
    const { symbol, quantity, openPrice, latestPrice } = this.props;
    let color = null;
    if (openPrice > latestPrice) {
      color = red;
    } else if (openPrice < latestPrice) {
      color = green;
    }
    return (
      <Row className="share" style={color}>
        <Col span={16}>
          <div>
            {symbol} - {quantity} Shares
          </div>
        </Col>
        <Col span={8}>
          <div className="price">
            {latestPrice ? (
              `$${latestPrice}`
            ) : (
              <Icon type="loading" style={{ fontSize: 24 }} spin />
            )}
          </div>
        </Col>
        <Col>
          {/* <input size="4" maxLength="4" id={symbol}></input>
        <button onClick={this.handleSubmit(document.getElementById({symbol}))} span={8}>sell</button> */}
          <Form onSubmit={this.handleSubmit}>
            <h3 className="title">Cash </h3>
            <Input
              onChange={this.handleSymbolChange}
              size="large"
              placeholder="Ticker"
            />
            <Button htmlType="submit" block size="large">
              Buy Shares
            </Button>
          </Form>
        </Col>
      </Row>
    );
  }
}

const red = {
  color: 'red',
};

const green = {
  color: 'green',
};
