import React, { Component } from 'react'
import NewsItem from './NewItem';


export default class News extends React.Component {
    // state = {
    //     news: [],
    //   }
    
    //   componentDidMount() {
    //     fetch('https://cloud.iexapis.com/stable/stock/market/news/last/5?token=pk_299ef38f938f4c358be82a13dcce2f84')
    //       .then(response => response.json())
    //       .then(data => this.setState({ news: data }));
    //   }
    //   render() {
    //     const { news } = this.state;
    
    //     return (
    //       <div>
    //         <h1>Latest News</h1> <NewsItem/>
    //         {news.map(item => (
    //           <div key={item.id}>
    //             <h2>{item.headline}</h2>
    //             <p>{item.summary}</p>
    //             <a href={item.url} target="_blank" rel="noopener noreferrer">Read More</a>
    //           </div>
    //         ))}
    //       </div>
    //     );
    //   }





    constructor(){
        super();
        this.state={
            articles : [],
            loading : false
        }
    }
    async componentDidMount(){
        let url = "https://cloud.iexapis.com/stable/stock/market/news/last/5?token=pk_299ef38f938f4c358be82a13dcce2f84"
        let data=await fetch(url);
        let parseData= await data.json();
        console.log(parseData)
        
        this.setState({articles : parseData.articles})
    }

    render(){
        return(
            <div className='container my-3 '>
            <h2> Top Headlines</h2>
            <div className='row'>
            {this.state.articles.map((element)=>{
                return(
    
                        <div className='col-md-4'>
                            <NewsItem  title ={element.headline} description={element.description} imageUrl={element.urlToImage} newsUrl={ element.url}/>
                        </div>
                )
            }
            )}
               
                
            </div>
            </div>
        )
    }
    }
