import React, { Component } from 'react'
import NewsItem from './NewItem';


export default class News extends Component {
    state = {
        news: [],
      }
    
      componentDidMount() {
        fetch('https://cloud.iexapis.com/stable/stock/market/news/last/5?token=pk_299ef38f938f4c358be82a13dcce2f84')
          .then(response => response.json())
          .then(data => this.setState({ news: data }));
      }
      render() {
        const { news } = this.state;
    
        return (
          <div>
            <h1>Latest News</h1> 
            <div className='row'> <NewsItem/>
            {news.map(item => (
               <div key={item.id}>
                <h2>{item.headline}</h2>
                <p>{item.summary}</p>
                <a href={item.url} target="_blank" rel="noopener noreferrer">Read More</a>
              </div> 
             
              
            ))
            }
            </div>
          </div>
        );
      }





